import pytestfrom beopWeb.mod_admin import controllersimport json@pytest.mark.p0@pytest.mark.parametrize(('data', 'expected'), [    (None, None),    ({"userEmail": 'projecttest@rnbtech.com.hk'}, 2265)])def test_select_searchUseId(data, expected):    rt = controllers.do_search_userId_by_email(data)    rt = json.loads(bytes.decode(rt.data))    if expected is not None:        assert rt.get('data').get('id') == expected, 'search userId by email fail,expected is {0},actual is {1}'.format(            expected, rt.get('data').get('id'))    else:        assert rt.get('data')==expected,'actual is not None'