import pytestfrom beopWeb.mod_admin import controllersfrom beopWeb.mod_admin.FavoriteProject import FavoriteProjectuserId=2265@pytest.mark.p0@pytest.mark.usefixtures("removeFavoriteProject")@pytest.mark.parametrize(('user_id','req'), [    (userId,{"projectId":49}),])def test_setFavoriteProject(user_id,req):    rt = controllers.do_set_favorite_project(user_id,req)    fp = FavoriteProject(user_id)    project_id=fp.get_favorite_project()    assert project_id.get("id")==req.get("projectId"),'set favorite project fail,expected is {0},actual is {1}'.format(req.get("projectId"),project_id.get("id"))@pytest.fixture(scope='function')def removeFavoriteProject(request):    def fin():        controllers.do_remove_favorite_project(userId)    request.addfinalizer(fin)