import pytestfrom beopWeb.mod_admin import controllersfrom beopWeb.mod_admin import Userimport jsonfrom beopWeb.mod_oss.ossapi import *from beopWeb.mod_common.Utils import *from flask import current_appname="2.png"@pytest.mark.p0@pytest.mark.parametrize(('req','expected'), [    ({"account":"2899718379@qq.com"},''),    ({"account":"projecttest@rnbtech.com.hk"},'EMAIL_IS_USED'),    ({"account":"18916409043"},'PHONE_NUM_IS_USED'),    ({"account":"18911111111"},''),    ({"account":"rtt"},'NOT_SUPPORT_THIS_ACCOUNT'),])def test_verifyCode(req,expected):    if not current_app:        app_ctx = app.app_context()        app_ctx.push()    rt=controllers.do_v_gen_verify_code(req)    rt=json.loads(bytes.decode(rt.data))    if expected:        assert rt.get('success')==False,'varify code fail'    else:        assert rt.get('success') == True, 'varify code fail'    assert rt.get('msg') == expected, 'not equal,acutal msg is{0},expected msg is {1}'.format(rt.get('msg'),                                                                                                  expected)